mapper.delete:
DELETE FROM Scope
WHERE scope_id = #{scopeId}
AND client_id = #{clientId}

mapper.select:
select scope_id
, client_id
, description
, is_default
from Scope
where client_id = #{clientId}

    AND scope_id = #{scopeId}


    AND is_default != #{isDefault}

mapper.update:
UPDATE Scope
SET description = #{description}
  , is_default = #{isDefault}
where scope_id = #{scopeId}
AND client_id = #{clientId}
