mapper.delete:
DELETE FROM Scope
 WHERE scope_id = #{ScopeId}
   AND client_id = #{clientId}

mapper.update:
UPDATE FROM Scope
   SET description = #{description}
 where scope_id = #{scopeId}
   AND client_id = #{clientId}

mapper.select:
select scope_id
     , client_id
     , description
  from Scope
 where client_id = #{clientId}
   AND scope_id = #{scopeId}
