mapper.select:
SELECT user_id, scope_id, client_id FROM UserClientScope
#{user}
order by client_id

SELECT count(*)
  FROM UserClientScope
 WHERE user_id = #{userCode}

SELECT user_id,
       scope_id,
       client_id
  FROM UserClientScope
 where user_id = #{userCode}

    and scope_id = #{scopeId}

   and client_id = #{clientId}

mapper.delete:
DELETE FROM UserClientScope
 WHERE user_id = #{userCode}
   AND client_id = #{clientId}

mapper.select:
SELECT count(*)
FROM UserClientScope
where user_id = #{userCode}
  AND client_id = #{clientId}

SELECT scope_id
FROM UserClientScope
where user_id = #{userCode}
AND client_id = #{clientId}

mapper.delete:
DELETE FROM UserClientScope
WHERE client_id = #{clientId}
  and scope_id = #{scopeId}

DELETE FROM UserClientScope
WHERE client_id = #{clientId}
  and scope_id = #{scopeId}
  and user_id = #{userCode}
