mapper.select:
SELECT count(*)
  FROM UserClientScope
 WHERE user_id = #{userId}

SELECT user_id,
       scope_id,
       client_id
  FROM UserClientScope
 where user_id = #{userId}

    and scope_id = #{scopeId}

   and client_id = #{clientId}

mapper.delete:
DELETE FROM UserClientScope
 WHERE user_id = #{userId}
   AND client_id = #{clientId}

mapper.select:
SELECT count(*)
FROM UserClientScope
where user_id = #{userId}
  AND client_id = #{clientId}

SELECT scope_id
FROM UserClientScope
where user_id = #{userId}
AND client_id = #{clientId}
