mapper.select:
SELECT user_id,
       email,
       name,
       register_date,
       phone,
       address,
       company
 FROM User

 WHERE user_id = #{_parameter}
     or email = #{_parameter}
     or company = #{_parameter}
     or name = #{_parameter}

mapper.insert:
